    private static IBlockState getCasingState() {
        return GTLiteMetaBlocks.SCIENCE_CASING.getState(BlockScienceCasing.ScienceCasingType.SINGULARITY_REINFORCED_STELLAR_SHIELDING_CASING);
    }

    private static IBlockState getSecondCasingState() {
        return GTLiteMetaBlocks.SCIENCE_CASING.getState(BlockScienceCasing.ScienceCasingType.CELESTIAL_MATTER_GUIDANCE_CASING);
    }

    private static IBlockState getThirdCasingState() {
        return GTLiteMetaBlocks.SCIENCE_CASING.getState(BlockScienceCasing.ScienceCasingType.BOUNDLESS_GRAVITATIONALLY_SEVERED_STRUCTURE_CASING);
    }

    private static IBlockState getFourthCasingState() {
        return GTLiteMetaBlocks.SCIENCE_CASING.getState(BlockScienceCasing.ScienceCasingType.TRANSCENDENTALLY_AMPLIFIED_MAGNETIC_CONFINEMENT_CASING);
    }

    private static IBlockState getFrameState() {
        return MetaBlocks.FRAMES.get(GTLiteMaterials.TranscendentMetal).getBlock(GTLiteMaterials.TranscendentMetal);
    }

    private static IBlockState getGlassState() {
        return GTLiteMetaBlocks.TRANSPARENT_CASING.getState(BlockTransparentCasing.TransparentCasingType.SPATIALLY_TRANSCENDENT_GRAVITATIONAL_LENS);
    }

    @SideOnly(Side.CLIENT)
    @Override
    public ICubeRenderer getBaseTexture(IMultiblockPart iMultiblockPart) {
        return iMultiblockPart == null ? GTLiteTextures.TRANSCENDENTALLY_AMPLIFIED_MAGNETIC_CONFINEMENT_CASING : GTLiteTextures.SINGULARITY_REINFORCED_STELLAR_SHIELDING_CASING;
    }

    @SideOnly(Side.CLIENT)
    @Nonnull
    @Override
    protected ICubeRenderer getFrontOverlay() {
        return GTLiteTextures.NICOLL_DYSON_BEAMER_OVERLAY;
    }

    @Override
    public void addInformation(ItemStack stack,
                               @Nullable World player,
                               @Nonnull List<String> tooltip,
                               boolean advanced) {
        super.addInformation(stack, player, tooltip, advanced);
        tooltip.add(I18n.format("gtlitecore.machine.nicoll_dyson_beamer.tooltip.1"));
        tooltip.add(I18n.format("gtlitecore.machine.nicoll_dyson_beamer.tooltip.2"));
        tooltip.add(I18n.format("gtlitecore.machine.nicoll_dyson_beamer.tooltip.3"));
        tooltip.add(I18n.format("gtlitecore.machine.nicoll_dyson_beamer.tooltip.4"));
        tooltip.add(I18n.format("gtlitecore.machine.nicoll_dyson_beamer.tooltip.5"));
    }

    @Override
    public boolean canBeDistinct() {
        return true;
    }
